function dataFormatEntryPoint(data) {
    try {
        if (data instanceof Uint8Array) {
            data[0] === 0x00
            ? showSYS(data): data[0] === 0x01
            ? showEEPROM(data): data[0] === 0x02
            ? showUserData(data): 0;
            return;
        }
    } catch (e) {}
    var ii;
    for (i in data) {
        if (i === "tx") {
            ii = $("tx");
            // console.log(data[i]);

            if (typeof data[i] === "object") {
                let state = getState(data[i]);
                if (state !== undefined) {
                    window.STATE = state;
                    updateSensorStatus(STATE.data);
                    updateSystemStatus(STATE);
                    updateStorageStatus();
                    updateGasResidual(STATE.data.hx711);
                    updateSensorCorrection(STATE.config);
                    updateChart(STATE.data);
                }
            } else {
                str = ii.value;
                str += '\n';
                str += data[i];
                ii.value = str;
                ii.scrollTop = ii.scrollHeight;

                let msg = data[i].toString();

                if (["ESP:set", "ESP:SET"].some((el) => msg.includes(el))) {
                    alert("更換SSID成功，reset後生效");
                }

                if (msg === "reconnected") {
                    alert("已重新連上wifi!");
                }

                if (msg === "reset") {
                    alert("已切換至獨立模式!");
                }

                if (msg.includes("set Device ID:")) {
                    alert("已修改裝置名稱!");
                }

                if (msg.includes("set Serial baudrate")) {
                    alert("已修改裝置 UART Baud!");
                }

                if (msg.includes("set correct baudrate")) {
                    alert("請設定支援的Baud: 9600,19200,38400,74800,115200,230400");
                }
                if (msg.includes("|")) {
                    res_wifi = data[i];
                }
                continue;
            }
        }
        ii = $(i);
        if (ii) {
            ii.innerHTML = data[i];
        }
        if (i === "device") {
            $("system_device").innerHTML = data[i];
        }
    }
}

function getState(state) {
    console.log(state);
    try {
        let newState = {};
        newState['data'] = {};
        newState['config'] = {};
        for (let key in state['data']) {
            newState['data'][key] = state['data'][key];
        }
        for (let key in state['config']) {
            newState['config'][key] = state['config'][key];
        }
        for (let key in state) {
            if (key !== 'data' && key !== 'config') {
                newState[key] = state[key];
            }
        }
        return newState;
    } catch (e) {
        return undefined;
    }
}

function updateSensorStatus(data) {
    let exclude = ["hall",
        "heater",
        "buzzer",
        "valveOpen",
        "valveClose",
        "valveState"];
    for (let key in data) {
        if (exclude.includes(key)) continue;
        $(key).innerText = data[key];
    }
}

function updateSystemStatus(state) {
    let status = ["hall",
        "heater",
        "buzzer",
        "valveState"];
    let rules = ["gas_leak",
        "gas_inbox",
        "gas_outbox",
        "co_outbox"];

    for (let key in state["data"]) {
        if (status.includes(key)) {
            if (parseInt(state["data"][key])) {
                //ON
                $("sys_" + key).innerText = "ON";
                $("sys_" + key).classList.remove("success");
                $("sys_" + key).classList.add("danger");
            } else {
                //OFF
