function updateGasResidual(grams) {
    let empty_weight = localStorage.getItem("gas_empty_weight");
    let total_weight = localStorage.getItem("gas_total_weight");
    if (isNaN(parseInt(empty_weight)) || isNaN(parseInt(total_weight))) return;

    if (grams > total_weight * 1000) return;

    let weight = Math.round(grams / 1000);
    let residual = Math.round(((weight - empty_weight) / (total_weight - empty_weight)) * 10)/10;

    residual = residual <= 0 ? 0: residual * 100;
    $("gas_percent").innerText = residual;

    // update progress bar
    $("gas_bar").style.height = (192 - (residual * 1.92)) + "px";

    if (residual >= gas_residual_normal) {
        $("progress").style.backgroundColor = "#77D88C";
        $("gas_percent").style.backgroundColor = "#77D88C";
    } else if (residual >= gas_residual_less && residual < gas_residual_normal) {
        $("progress").style.backgroundColor = "#FFD24B";
        $("gas_percent").style.backgroundColor = "#FFD24B";
    } else if (residual < gas_residual_less) {
        $("progress").style.backgroundColor = "#FF5F55";
        $("gas_percent").style.backgroundColor = "#FF5F55";
    }
}

